   215‚Üí      context ? `CONTEXT:\n${context}\n` : '',
   216‚Üí      `REQUEST:\n${prompt}`
   217‚Üí    ].filter(Boolean).join('\n');
   218‚Üí
   219‚Üí    // –ò—Å–ø–æ–ª—å–∑—É–µ–º –≤—ã–±—Ä–∞–Ω–Ω—É—é –º–æ–¥–µ–ª—å –∏–ª–∏ –¥–µ—Ñ–æ–ª—Ç–Ω—É—é
   220‚Üí    const selectedModel = model || 'gemini-2.5-flash';
   221‚Üí    log(`Using model: ${selectedModel}`);
   222‚Üí    
   223‚Üí    // –î–ª—è streaming –æ—Ç–≤–µ—Ç–æ–≤ - –í–†–ï–ú–ï–ù–ù–û –û–¢–ö–õ–Æ–ß–ï–ù–û –∏–∑-–∑–∞ –æ—à–∏–±–∫–∏ API
   224‚Üí    // TODO: –ò—Å–ø—Ä–∞–≤–∏—Ç—å –∫–æ–≥–¥–∞ Google –ø–æ—á–∏–Ω–∏—Ç generateContentStream
   225‚Üí    /*
   226‚Üí    if (req.body.stream) {
   227‚Üí      // Streaming –ø–æ–∫–∞ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç —Å —Ç–µ–∫—É—â–µ–π –≤–µ—Ä—Å–∏–µ–π API
   228‚Üí    }
   229‚Üí    */
   230‚Üí    
   231‚Üí    // –ò—Å–ø–æ–ª—å–∑—É–µ–º forge_mode –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—é –µ—Å–ª–∏ –≤–∫–ª—é—á–µ–Ω
   232‚Üí    const config = geminiConfig.forge_mode.enabled ? geminiConfig.forge_mode : geminiConfig.default_generation_config;
   233‚Üí    
   234‚Üí    // –û–±—ã—á–Ω—ã–π —Ä–µ–∂–∏–º - –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –∏ –∑–∞–±—ã–≤–∞–µ–º
   235‚Üí    const genModel = genAI.getGenerativeModel({ 
   236‚Üí      model: selectedModel,
   237‚Üí      generationConfig: {
   238‚Üí        temperature: temperature !== undefined ? temperature : config.temperature,
   239‚Üí        topK: topK !== undefined ? topK : config.topK,
   240‚Üí        topP: topP !== undefined ? topP : config.topP,
   241‚Üí        maxOutputTokens: maxTokens || config.maxOutputTokens,
   242‚Üí        stopSequences: config.stopSequences || [],
   243‚Üí      }
   244‚Üí    });
   245‚Üí    
   246‚Üí    log(`Generation config: temp=${temperature ?? config.temperature}, topK=${topK ?? config.topK}, topP=${topP ?? config.topP}`);
   247‚Üí    const result = await genModel.generateContent(fullPrompt);
   248‚Üí    
   249‚Üí    // –ü—Ä–æ–≤–µ—Ä—è–µ–º —á—Ç–æ –µ—Å—Ç—å –æ—Ç–≤–µ—Ç
   250‚Üí    const response = result.response.text();
   251‚Üí
   252‚Üí    // –û—Ç–¥–∞–µ–º –æ—Ç–≤–µ—Ç –∏ —Ä–∞–∑—Ä—ã–≤–∞–µ–º —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ
   253‚Üí    res.json({ response });
   254‚Üí    
   255‚Üí    // –£–±–∏–≤–∞–µ–º –≤—Å–µ —Å—Å—ã–ª–∫–∏
   256‚Üí    delete genAI;
   257‚Üí    delete model;
