   259‚Üí
   260‚Üí  } catch (err) {
   261‚Üí    error('API Error:', err.message);
   262‚Üí    error('Full error:', err);
   263‚Üí    
   264‚Üí    // If quota exceeded, mark key and retry with next one
   265‚Üí    if (err.message && (err.message.includes('quota') || err.message.includes('limit'))) {
   266‚Üí      keyRotator.markKeyAsExhausted(apiKey);
