// –û–¥–∏–Ω endpoint - –∏ –≤—Å—ë
app.post('/chat', async (req, res) => {
  try {
    const { prompt, instruction, context, model, temperature, topK, topP, maxTokens, useMemory } = req.body;

    if (!prompt) {
      return res.status(400).json({ error: 'No prompt provided' });
    }

    // –ü–æ–ª—É—á–∞–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç –∏–∑ –ø–∞–º—è—Ç–∏ –µ—Å–ª–∏ –≤–∫–ª—é—á–µ–Ω–æ
    let memoryContext = '';
    if (useMemory && memoryAPIAvailable) {
      try {
        const contextResponse = await axios.get(`${MEMORY_API_URL}/get_context?limit=5`);
        if (contextResponse.data.context && contextResponse.data.context.length > 0) {
          memoryContext = '\n\n–ü–†–ï–î–´–î–£–©–ò–ô –ö–û–ù–¢–ï–ö–°–¢:\n';
          contextResponse.data.context.forEach(c => {
            memoryContext += `[${c.timestamp}]\nUser: ${c.user}\nAssistant: ${c.assistant}\n\n`;
          });
        }
      } catch (err) {
        error('Failed to get memory context:', err.message);
      }
    }

    // –ö–∞–∂–¥—ã–π —Ä–∞–∑ –Ω–æ–≤—ã–π –∫–ª–∏–µ–Ω—Ç –∏ –Ω–æ–≤—ã–π –∫–ª—é—á
    let apiKey;
    try {
      apiKey = keyRotator.getNextValidKey();
      log(`Using key: ${apiKey.substring(0, 10)}...${apiKey.substring(apiKey.length - 4)}`);
    } catch (e) {
      // –ò—Å–ø–æ–ª—å–∑—É–µ–º –∫–ª—é—á –∏–∑ –æ–∫—Ä—É–∂–µ–Ω–∏—è –µ—Å–ª–∏ —Ä–æ—Ç–∞—Ç–æ—Ä –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç
      apiKey = process.env.GEMINI_API_KEY || process.env.GOOGLE_API_KEY;
      if (!apiKey) {
        error('ERROR: No API keys available! Set GEMINI_API_KEY environment variable');
        return res.status(500).json({ error: 'No API keys available. Please set GEMINI_API_KEY' });
      }
      log(`Using environment key: ${apiKey.substring(0, 10)}...`);
    }

    const genAI = new GoogleGenerativeAI(apiKey);

    // –°–æ–±–∏—Ä–∞–µ–º –ø–æ–ª–Ω—ã–π –ø—Ä–æ–º–ø—Ç —Å –ø–∞–º—è—Ç—å—é
    const fullPrompt = [
      instruction ? `INSTRUCTION:\n${instruction}\n` : '',
      memoryContext,  // –î–æ–±–∞–≤–ª—è–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç –∏–∑ –ø–∞–º—è—Ç–∏
      context ? `CONTEXT:\n${context}\n` : '',
      `REQUEST:\n${prompt}`
    ].filter(Boolean).join('\n');

    // –ò—Å–ø–æ–ª—å–∑—É–µ–º –≤—ã–±—Ä–∞–Ω–Ω—É—é –º–æ–¥–µ–ª—å –∏–ª–∏ –¥–µ—Ñ–æ–ª—Ç–Ω—É—é
    const selectedModel = model || 'gemini-2.5-flash';
    log(`Using model: ${selectedModel}`);

    // –î–ª—è streaming –æ—Ç–≤–µ—Ç–æ–≤ - –í–†–ï–ú–ï–ù–ù–û –û–¢–ö–õ–Æ–ß–ï–ù–û –∏–∑-–∑–∞ –æ—à–∏–±–∫–∏ API
    // TODO: –ò—Å–ø—Ä–∞–≤–∏—Ç—å –∫–æ–≥–¥–∞ Google –ø–æ—á–∏–Ω–∏—Ç generateContentStream
    /*
    if (req.body.stream) {
      // Streaming –ø–æ–∫–∞ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç —Å —Ç–µ–∫—É—â–µ–π –≤–µ—Ä—Å–∏–µ–π API
    }
    */

    // –ò—Å–ø–æ–ª—å–∑—É–µ–º forge_mode –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—é –µ—Å–ª–∏ –≤–∫–ª—é—á–µ–Ω
    const config = geminiConfig.forge_mode.enabled ? geminiConfig.forge_mode : geminiConfig.default_generation_config;

    // –û–±—ã—á–Ω—ã–π —Ä–µ–∂–∏–º - –æ—Ç–ø—Ä–∞–≤–ª—è–µ–º –∏ –∑–∞–±—ã–≤–∞–µ–º
    const genModel = genAI.getGenerativeModel({ 
      model: selectedModel,
      generationConfig: {
        temperature: temperature !== undefined ? temperature : config.temperature,
        topK: topK !== undefined ? topK : config.topK,
        topP: topP !== undefined ? topP : config.topP,
        maxOutputTokens: maxTokens || config.maxOutputTokens,
        stopSequences: config.stopSequences || [],
      }
    });

    log(`Generation config: temp=${temperature ?? config.temperature}, topK=${topK ?? config.topK}, topP=${topP ?? config.topP}`);
    const result = await genModel.generateContent(fullPrompt);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º —á—Ç–æ –µ—Å—Ç—å –æ—Ç–≤–µ—Ç
    const response = result.response.text();

    // –û—Ç–¥–∞–µ–º –æ—Ç–≤–µ—Ç –∏ —Ä–∞–∑—Ä—ã–≤–∞–µ–º —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ
    res.json({ response });

    // –£–±–∏–≤–∞–µ–º –≤—Å–µ —Å—Å—ã–ª–∫–∏
    delete genAI;
    delete model;
    delete result;

  } catch (err) {
    error('API Error:', err.message);
    error('Full error:', err);

    // If quota exceeded, mark key and retry with next one
    if (err.message && (err.message.includes('quota') || err.message.includes('limit'))) {
      keyRotator.markKeyAsExhausted(apiKey);
      error('Key exhausted, will use next one on next request');
    }

    res.status(500).json({ error: err.message });
  }
});

const PORT = process.env.PORT || 37777;
app.listen(PORT, '127.0.0.1', () => {
  error(`Backend running on http://127.0.0.1:${PORT}`);
  error('POST /chat - {prompt, instruction, context}');
});

// Graceful shutdown
process.on('SIGTERM', () => process.exit(0));
process.on('SIGINT', () => process.exit(0));

