app.post('/element-selected', (req, res) => {
  const data = req.body;
  if (data) {
    // –°–æ—Ö—Ä–∞–Ω—è–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è webhook
    lastElementData = data;

    // –í—ã–≤–æ–¥–∏–º –ø–æ–ª—É—á–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –≤ –∫–æ–Ω—Å–æ–ª—å —á—Ç–æ–±—ã Claude –∏—Ö —É–≤–∏–¥–µ–ª
    error('=== ELEMENT DATA FOR CLAUDE ===');
    error(JSON.stringify(data, null, 2));
