/* ====== FORGE MEMORY STYLES ====== */
.memory-title {
    color: #ff6b6b;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-top: 20px;
    margin-bottom: 15px;
    text-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
    animation: forge-pulse 2s ease-in-out infinite;
}

@keyframes forge-pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.8; }
}

.memory-controls {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.05) 0%, rgba(139, 92, 246, 0.05) 100%);
    border-radius: 12px;
    padding: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
}

.memory-toggle {
    margin-bottom: 12px;
}

/* FORGE Style Toggle Switch */
.forge-switch {
    position: relative;
    display: block;
    width: 100%;
    height: 45px;
    cursor: pointer;
}

.forge-switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

.forge-slider {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(30, 30, 35, 0.9) 0%, rgba(40, 40, 45, 0.9) 100%);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 8px;
    transition: all 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
    overflow: hidden;
    display: flex;
    align-items: center;
    padding: 0 15px;
}

.forge-slider::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #ff6b6b 0%, #ff8787 50%, #feca57 100%);
    transition: left 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
    opacity: 0.3;
}

.forge-switch input:checked + .forge-slider {
    background: linear-gradient(135deg, rgba(255, 107, 107, 0.1) 0%, rgba(255, 135, 135, 0.1) 100%);
