// Load settings from localStorage
window.addEventListener('load', () => {
    const savedInstruction = localStorage.getItem('galaxydevelopers-ai-instruction');
    const savedContext = localStorage.getItem('galaxydevelopers-ai-context');
    const savedModel = localStorage.getItem('galaxydevelopers-ai-model');

    if (savedInstruction && instructionTextarea) instructionTextarea.value = savedInstruction;
    if (savedContext && contextEl) contextEl.value = savedContext;
    if (savedModel && modelEl) modelEl.value = savedModel;

    // –ò–ù–ò–¶–ò–ê–õ–ò–ó–ê–¶–ò–Ø PROXIMITY –ü–ê–ù–ï–õ–ò
    initPanel();
    animatePanel();

    // –ò–ù–ò–¶–ò–ê–õ–ò–ó–ê–¶–ò–Ø –ù–ê–°–¢–†–û–ï–ö –ì–ï–ù–ï–†–ê–¶–ò–ò
    initGenerationSettings();

    promptInput.focus();
});

// –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è –Ω–∞—Å—Ç—Ä–æ–µ–∫ –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏
function initGenerationSettings() {
    // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∑–Ω–∞—á–µ–Ω–∏–π —Å–ª–∞–π–¥–µ—Ä–æ–≤
    if (temperatureSlider) {
        temperatureSlider.addEventListener('input', (e) => {
            tempValue.textContent = parseFloat(e.target.value).toFixed(1);
        });
    }

    if (topPSlider) {
        topPSlider.addEventListener('input', (e) => {
            topPValue.textContent = parseFloat(e.target.value).toFixed(1);
        });
    }

    // –ö–Ω–æ–ø–∫–∏ —Ä–µ–∂–∏–º–æ–≤
    const forgeModeBtn = document.getElementById('forge-mode-btn');
    const creativeModeBtn = document.getElementById('creative-mode-btn');
    const balancedModeBtn = document.getElementById('balanced-mode-btn');

    if (forgeModeBtn) {
        forgeModeBtn.addEventListener('click', () => setGenerationMode('forge'));
        creativeModeBtn.addEventListener('click', () => setGenerationMode('creative'));
        balancedModeBtn.addEventListener('click', () => setGenerationMode('balanced'));
    }
}

function setGenerationMode(mode) {
    document.querySelectorAll('.mode-btn').forEach(btn => btn.classList.remove('active'));

    switch(mode) {
        case 'forge':
            document.getElementById('forge-mode-btn').classList.add('active');
            temperatureSlider.value = 0;
            tempValue.textContent = '0.0';
            topKInput.value = 1;
