// Model Selector Popup
let currentModel = "gemini-2.5-flash";
let modelPopup = null;
let modelSelectorBtn = null;
let modelGrid = null;

document.addEventListener("DOMContentLoaded", function() {
    modelPopup = document.getElementById("model-popup");
    modelSelectorBtn = document.getElementById("model-selector-btn");
    modelGrid = document.querySelector(".model-grid");

    // Load models from server
    loadModels();

    // Toggle popup on button click
    if (modelSelectorBtn) {
        modelSelectorBtn.addEventListener("click", (e) => {
            e.stopPropagation();
            toggleModelPopup();
        });
    }

    // Close popup on X button
    const closeBtn = document.querySelector(".model-popup-close");
    if (closeBtn) {
        closeBtn.addEventListener("click", () => {
            hideModelPopup();
        });
    }

    // Close popup on outside click
    document.addEventListener("click", (e) => {
        if (modelPopup && !modelPopup.contains(e.target) && e.target !== modelSelectorBtn) {
            hideModelPopup();
        }
    });
});

async function loadModels() {
    try {
        const response = await fetch("/models");
        const data = await response.json();

        if (data.models) {
            renderModels(data.models, data.forge_recommended);
        }
    } catch (err) {
        console.error("Failed to load models:", err);
    }
}

function renderModels(models, recommendedModel) {
    if (!modelGrid) return;

    modelGrid.innerHTML = "";

    models.forEach(model => {
        const card = document.createElement("div");
        card.className = "model-card";

        if (model.name === recommendedModel) {
            card.classList.add("recommended");
        }

        if (model.name === currentModel) {
            card.classList.add("selected");
        }

        card.innerHTML = `
            <div class="model-card-name">${model.displayName}</div>
            <div class="model-card-desc">${model.description}</div>
            <div class="model-card-tokens">${(model.tokens / 1000).toFixed(0)}K tokens</div>
        `;

        card.addEventListener("click", () => {
            selectModel(model.name, model.displayName);
        });

        modelGrid.appendChild(card);
    });
}

function selectModel(modelName, displayName) {
    currentModel = modelName;

    // Update button text
    const modelNameSpan = document.querySelector(".current-model-name");
    if (modelNameSpan) {
        modelNameSpan.textContent = displayName;
    }

    // Update model select (for compatibility)
    const modelSelect = document.getElementById("model");
    if (modelSelect) {
        modelSelect.value = modelName;
    }

    // Update selected card
    document.querySelectorAll(".model-card").forEach(card => {
        card.classList.remove("selected");
    });

    event.currentTarget.classList.add("selected");

    // Animate and close
    setTimeout(() => {
        hideModelPopup();
    }, 200);
}

function toggleModelPopup() {
    if (modelPopup.classList.contains("show")) {
        hideModelPopup();
    } else {
        showModelPopup();
    }
}

function showModelPopup() {
    modelPopup.classList.add("show");
    modelSelectorBtn.classList.add("active");
}

function hideModelPopup() {
    modelPopup.classList.remove("show");
    modelSelectorBtn.classList.remove("active");
}

// Update sendMessage to use current model
const originalSendMessage = window.sendMessage;
if (originalSendMessage) {
    window.sendMessage = async function() {
        // Temporarily set model value
        const modelEl = document.getElementById("model");
        if (modelEl) {
            modelEl.value = currentModel;
        }
        return originalSendMessage.apply(this, arguments);
    };
}' >> /Volumes/Z7S/development/GalaxyDevelopers/DevSystem/interface/js/app.js

