:root {
    --bg-primary: #0a0a0a;
    --bg-secondary: #141414;
    --bg-tertiary: #1a1a1a;
    --bg-card: rgba(255, 255, 255, 0.03);
    --border-color: rgba(255, 255, 255, 0.12);
    --text-primary: #ffffff;
    --text-secondary: #a0a0a0;
    --text-muted: #666666;
    --accent-primary: #6366f1;
    --accent-secondary: #8b5cf6;
    --accent-gradient: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    --success: #10b981;
    --error: #ef4444;
    --warning: #f59e0b;
    --tab-height: 35px;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background: var(--bg-primary);
    color: var(--text-primary);
    height: 100vh;
    display: flex;
    flex-direction: column;
    position: relative;
}

body::before {
    content: '';
    position: fixed;
    top: -50%;
    right: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(99, 102, 241, 0.1) 0%, transparent 70%);
    pointer-events: none;
    animation: float 20s ease-in-out infinite;
}

@keyframes float {
    0%, 100% { transform: translate(0, 0) rotate(0deg); }
    33% { transform: translate(-30px, -30px) rotate(120deg); }
    66% { transform: translate(30px, -30px) rotate(240deg); }
}

.header {
    padding: 10px 30px;
    border-bottom: 1px solid var(--border-color);
    background: rgba(20, 20, 20, 0.8);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    position: relative;
    z-index: 100;
}

.header h1 {
    font-size: 20px;
    font-weight: 600;
    background: var(--accent-gradient);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    letter-spacing: -0.5px;
}

.wrapper {
    flex: 1;
    display: flex;
    overflow: hidden;
    position: relative;
}

.sidebar {
    width: 280px;
    background: rgba(20, 20, 20, 0.5);
    border-right: 1px solid var(--border-color);
    padding: 20px;
    overflow-y: auto;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.sidebar::-webkit-scrollbar {
    width: 6px;
}

.sidebar::-webkit-scrollbar-track {
    background: transparent;
}

.sidebar::-webkit-scrollbar-thumb {
    background: var(--border-color);
    border-radius: 3px;
}

.sidebar::-webkit-scrollbar-thumb:hover {
    background: var(--text-muted);
}

/* –ù–µ–æ–Ω–æ–≤–∞—è –ø–æ–ª–æ—Å–∞ —Å—Ç–∞—Ç—É—Å–∞ */
.status-strip {
    height: 3px;
    width: 100%;
    position: relative;
    overflow: hidden;
    background: transparent;
    border-bottom: 1px solid var(--border-color);
}

.status-strip::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--accent-primary) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(99, 102, 241, 0.5), 
               0 0 16px rgba(99, 102, 241, 0.3);
    transition: all 0.3s ease;
}

.status-strip.error::before {
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--error) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(239, 68, 68, 0.5), 
               0 0 16px rgba(239, 68, 68, 0.3);
}

.status-strip.warning::before {
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--warning) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(245, 158, 11, 0.5), 
               0 0 16px rgba(245, 158, 11, 0.3);
}

.status-strip.loading::before {
    background: linear-gradient(90deg, 
        transparent 0%, 
        var(--accent-primary) 50%, 
        transparent 100%);
    animation: statusFlow 2s ease-in-out infinite;
}

@keyframes statusFlow {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

/* Proximity —è–∑—ã—á–æ–∫ - –ü–û–õ–ù–û–°–¢–¨–Æ –°–ö–†–´–¢ –∏–∑–Ω–∞—á–∞–ª—å–Ω–æ */
.proximity-instruction {
    position: absolute;
    top: 0;
    left: 50%;
