:root {
    --bg-primary: #0a0a0a;
    --bg-secondary: #141414;
    --bg-tertiary: #1a1a1a;
    --bg-card: rgba(255, 255, 255, 0.03);
    --border-color: rgba(255, 255, 255, 0.12);
    --text-primary: #ffffff;
    --text-secondary: #a0a0a0;
    --text-muted: #666666;
    --accent-primary: #6366f1;
    --accent-secondary: #8b5cf6;
    --accent-gradient: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    --success: #10b981;
    --error: #ef4444;
    --warning: #f59e0b;
    --tab-height: 35px;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background: var(--bg-primary);
    color: var(--text-primary);
    height: 100vh;
    display: flex;
    flex-direction: column;
    position: relative;
}

body::before {
    content: '';
    position: fixed;
    top: -50%;
    right: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(99, 102, 241, 0.1) 0%, transparent 70%);
    pointer-events: none;
    animation: float 20s ease-in-out infinite;
}

@keyframes float {
    0%, 100% { transform: translate(0, 0) rotate(0deg); }
    33% { transform: translate(-30px, -30px) rotate(120deg); }
    66% { transform: translate(30px, -30px) rotate(240deg); }
}

.header {
    padding: 10px 30px;
    border-bottom: 1px solid var(--border-color);
    background: rgba(20, 20, 20, 0.8);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    position: relative;
    z-index: 100;
}

.header h1 {
    font-size: 20px;
    font-weight: 600;
    background: var(--accent-gradient);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    letter-spacing: -0.5px;
}

.wrapper {
    flex: 1;
    display: flex;
    overflow: hidden;
    position: relative;
}

.sidebar {
    width: 280px;
    background: rgba(20, 20, 20, 0.5);
    border-right: 1px solid var(--border-color);
    padding: 20px;
    overflow-y: auto;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.sidebar::-webkit-scrollbar {
    width: 6px;
}

.sidebar::-webkit-scrollbar-track {
    background: transparent;
}

.sidebar::-webkit-scrollbar-thumb {
    background: var(--border-color);
    border-radius: 3px;
}

.sidebar::-webkit-scrollbar-thumb:hover {
    background: var(--text-muted);
}

/* –ù–µ–æ–Ω–æ–≤–∞—è –ø–æ–ª–æ—Å–∞ —Å—Ç–∞—Ç—É—Å–∞ */
.status-strip {
    height: 3px;
    width: 100%;
    position: relative;
    overflow: hidden;
    background: transparent;
    border-bottom: 1px solid var(--border-color);
}

.status-strip::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--accent-primary) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(99, 102, 241, 0.5), 
               0 0 16px rgba(99, 102, 241, 0.3);
    transition: all 0.3s ease;
}

.status-strip.error::before {
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--error) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(239, 68, 68, 0.5), 
               0 0 16px rgba(239, 68, 68, 0.3);
}

.status-strip.warning::before {
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--warning) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(245, 158, 11, 0.5), 
               0 0 16px rgba(245, 158, 11, 0.3);
}

.status-strip.loading::before {
    background: linear-gradient(90deg, 
        transparent 0%, 
        var(--accent-primary) 50%, 
        transparent 100%);
    animation: statusFlow 2s ease-in-out infinite;
}

@keyframes statusFlow {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

/* Proximity —è–∑—ã—á–æ–∫ - –ü–û–õ–ù–û–°–¢–¨–Æ –°–ö–†–´–¢ –∏–∑–Ω–∞—á–∞–ª—å–Ω–æ */
.proximity-instruction {
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%) translateY(-100%);
    z-index: 60;
    overflow: visible;
    width: fit-content;
    min-width: 200px;
}

/* –Ø–∑—ã—á–æ–∫ */
.instruction-tab {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border: none;
    border-radius: 0 0 12px 12px;
    padding: 8px 24px;
    cursor: pointer;
    font-size: 12px;
    color: white;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    text-align: center;
    height: var(--tab-height);
    display: flex;
    align-items: center;
    justify-content: center;
    user-select: none;
    white-space: nowrap;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
    position: relative;
    overflow: hidden;
}

.instruction-tab::before {
    content: 'üìù';
    margin-right: 6px;
    font-size: 14px;
}

.instruction-tab::after {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s ease;
}

.instruction-tab:hover {
    background: linear-gradient(135deg, #764ba2 0%, #667eea 100%);
    transform: scale(1.05);
    box-shadow: 0 6px 20px rgba(102, 126, 234, 0.5);
}

.instruction-tab:hover::after {
    left: 100%;
}

/* –°–æ—Å—Ç–æ—è–Ω–∏–µ –∫–æ–≥–¥–∞ –ø–∞–Ω–µ–ª—å —Ä–∞—Å–∫—Ä—ã—Ç–∞ –ø–æ –∫–ª–∏–∫—É */
.proximity-instruction.expanded {
    width: 70%;
}

.proximity-instruction.expanded .instruction-tab {
    background: var(--bg-secondary);
    border-bottom: 1px solid var(--border-color);
    cursor: pointer;
}

.proximity-instruction.expanded .instruction-panel {
    display: block;
}


/* –ü–∞–Ω–µ–ª—å —Å textarea - —Å–∫—Ä—ã—Ç–∞ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é */
.instruction-panel {
    padding: 10px;
    display: none;
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 0 0 8px 8px;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.instruction-panel textarea {
    width: calc(100% - 20px);
    height: 100px;
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    padding: 10px;
    color: var(--text-primary);
    resize: vertical;
    font-family: inherit;
    font-size: 13px;
    outline: none;
}

.instruction-panel textarea:focus {
    outline: none;
    border-color: var(--accent-primary);
}

.instruction-panel textarea::placeholder {
    color: var(--text-secondary);
}

.content {
    flex: 1;
    display: flex;
    flex-direction: column;
    position: relative;
}



/* Generation Settings Panel */
.settings-block {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(139, 92, 246, 0.1) 100%);
    border-radius: 12px;
    padding: 16px;
    margin-bottom: 24px;
    border: 1px solid rgba(99, 102, 241, 0.2);
}

.setting-row {
    display: flex;
    align-items: center;
    padding: 10px 12px;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 8px;
    transition: all 0.3s ease;
    border: 1px solid transparent;
    margin-bottom: 8px;
}

.setting-row:hover {
    background: rgba(255, 255, 255, 0.05);
    border-color: rgba(99, 102, 241, 0.3);
    transform: translateX(4px);
}

.setting-label {
    width: 100px;
    font-size: 11px;
    font-weight: 500;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.setting-row input[type="range"] {
    flex: 1;
    height: 4px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    outline: none;
    margin: 0 12px;
}

.setting-row input[type="range"]::-webkit-slider-thumb {
    width: 16px;
    height: 16px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border-radius: 50%;
    cursor: pointer;
    -webkit-appearance: none;
    box-shadow: 0 2px 8px rgba(99, 102, 241, 0.4);
    transition: all 0.2s ease;
}

.setting-row input[type="range"]::-webkit-slider-thumb:hover {
    transform: scale(1.2);
    box-shadow: 0 4px 12px rgba(99, 102, 241, 0.6);
}

.setting-row input[type="number"] {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(99, 102, 241, 0.3);
    color: var(--text-primary);
    padding: 6px 10px;
    border-radius: 6px;
    width: 80px;
    font-size: 11px;
    transition: all 0.2s ease;
}

.setting-row input[type="number"]:focus {
    border-color: rgba(99, 102, 241, 0.6);
    background: rgba(255, 255, 255, 0.08);
    outline: none;
}

.value, #temp-value, #topP-value {
    color: var(--accent-primary);
    font-size: 11px;
    min-width: 35px;
    text-align: right;
    font-weight: 600;
}

/* Mode Buttons */
.mode-buttons {
    display: flex;
    gap: 8px;
    margin-bottom: 24px;
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.1) 0%, rgba(245, 158, 11, 0.1) 100%);
    border-radius: 12px;
    padding: 16px;
    border: 1px solid rgba(239, 68, 68, 0.2);
}

.mode-btn {
    flex: 1;
    padding: 10px 8px;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid transparent;
    border-radius: 8px;
    color: var(--text-secondary);
    font-size: 11px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.mode-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
    transition: left 0.5s ease;
}

.mode-btn:hover {
    background: rgba(255, 255, 255, 0.05);
    border-color: rgba(239, 68, 68, 0.3);
    transform: translateY(-2px);
}

.mode-btn:hover::before {
    left: 100%;
}

.mode-btn.active {
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.2) 0%, rgba(245, 158, 11, 0.2) 100%);
