input.script:
// –ü—Ä–æ–≤–µ—Ä—è–µ–º —ç–ª–µ–º–µ–Ω—Ç—ã
const result = {
    proximityInstruction: !!document.getElementById('proximity-instruction'),
    instructionTab: !!document.getElementById('instruction-tab'),
    instructionPanel: !!document.getElementById('instruction-panel'),
    instructionTextarea: !!document.getElementById('instruction-textarea')
};

// –ü—Ä–æ–±—É–µ–º –ø–µ—Ä–µ–∫–ª—é—á–∏—Ç—å
const container = document.getElementById('proximity-instruction');
if (container) {
    container.classList.toggle('expanded');
    result.toggled = true;
} else {
    result.toggled = false;
}

JSON.stringify(result);

// –ü—Ä–æ–≤–µ—Ä—è—é —Ä–∞–±–æ—Ç—É —á–∞—Ç–∞ - –æ—Ç–ø—Ä–∞–≤–ª—è—é —Ç–µ—Å—Ç–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ
const input = document.getElementById('prompt-input');
const sendBtn = document.getElementById('send-btn');

if (input && sendBtn) {
    input.value = '–ü—Ä–∏–≤–µ—Ç, —ç—Ç–æ —Ç–µ—Å—Ç–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ';
    sendBtn.click();

    setTimeout(() => {
        const messages = document.querySelectorAll('.message');
        const lastMessage = messages[messages.length - 1];
        console.log('Last message:', lastMessage ? lastMessage.textContent : 'No messages');

        // –ü—Ä–æ–≤–µ—Ä—è—é —Å—Ç–∞—Ç—É—Å
        const statusStrip = document.getElementById('status-strip');
        console.log('Status:', statusStrip ? statusStrip.textContent : 'No status');
    }, 2000);

    'Message sent';
} else {
    'Elements not found: input=' + !!input + ', sendBtn=' + !!sendBtn;
}

// –ü—Ä–æ–≤–µ—Ä—è—é –Ω–∞–ª–∏—á–∏–µ –∫–Ω–æ–ø–∫–∏ –≤—ã–±–æ—Ä–∞ –º–æ–¥–µ–ª–∏
const modelBtn = document.getElementById('model-selector-btn');
const modelPopup = document.getElementById('model-popup');

console.log('Model button exists:', !!modelBtn);
console.log('Model popup exists:', !!modelPopup);

if (modelBtn) {
    console.log('Button HTML:', modelBtn.outerHTML);
    console.log('Button visible:', window.getComputedStyle(modelBtn).display !== 'none');
    console.log('Button position:', modelBtn.getBoundingClientRect());
}

'Model button: ' + !!modelBtn + ', Popup: ' + !!modelPopup;

