/* ====== FORGE MEMORY STYLES ====== */
.memory-title {
    color: #ff6b6b;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-top: 20px;
    margin-bottom: 15px;
    text-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
    animation: forge-pulse 2s ease-in-out infinite;
}

@keyframes forge-pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.8; }
}

.memory-controls {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.05) 0%, rgba(139, 92, 246, 0.05) 100%);
    border-radius: 12px;
    padding: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
}

.memory-toggle {
    margin-bottom: 12px;
}

/* FORGE Style Toggle Switch */
.forge-switch {
    position: relative;
    display: block;
    width: 100%;
    height: 45px;
    cursor: pointer;
}

.forge-switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

.forge-slider {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(30, 30, 35, 0.9) 0%, rgba(40, 40, 45, 0.9) 100%);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 8px;
    transition: all 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
    overflow: hidden;
    display: flex;
    align-items: center;
    padding: 0 15px;
}

.forge-slider::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #ff6b6b 0%, #ff8787 50%, #feca57 100%);
    transition: left 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
    opacity: 0.3;
}

.forge-switch input:checked + .forge-slider {
    background: linear-gradient(135deg, rgba(255, 107, 107, 0.1) 0%, rgba(255, 135, 135, 0.1) 100%);
    border-color: rgba(255, 107, 107, 0.5);
    box-shadow: 0 0 20px rgba(255, 107, 107, 0.3);
}

.forge-switch input:checked + .forge-slider::before {
    left: 0;
}

.forge-label {
    position: relative;
    z-index: 2;
    font-size: 13px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
    transition: all 0.3s ease;
}

.forge-switch input:checked + .forge-slider .forge-label {
    color: #fff;
    text-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
}

/* FORGE Action Button */
.forge-action-btn {
    width: 100%;
    margin-top: 15px;
    padding: 12px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border: none;
    border-radius: 8px;
    color: white;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    transition: all 0.3s ease;
}

.forge-action-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transition: left 0.6s ease;
}

.forge-action-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(102, 126, 234, 0.4);
}

.forge-action-btn:hover::before {
    left: 100%;
}

.forge-btn-text {
    position: relative;
    z-index: 2;
}

/* –ê–Ω–∏–º–∞—Ü–∏—è –ø—Ä–∏ –∞–∫—Ç–∏–≤–∞—Ü–∏–∏ */
@keyframes forge-activate {
    0% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
    }
}

.forge-switch input:checked + .forge-slider {
    animation: forge-activate 0.3s ease;
}


/* ====== FORGE MODEL SELECTOR POPUP ====== */
.send-controls {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.model-selector-btn {
    padding: 10px 16px;
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(139, 92, 246, 0.1) 100%);
    border: 1px solid rgba(99, 102, 241, 0.3);
    border-radius: 10px;
    color: var(--text-primary);
    cursor: pointer;
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 12px;
    font-weight: 600;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.model-selector-btn::before {
    content: "";
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(99, 102, 241, 0.2), transparent);
    transition: left 0.6s ease;
}

.model-selector-btn:hover {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.2) 0%, rgba(139, 92, 246, 0.2) 100%);
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(99, 102, 241, 0.3);
}

.model-selector-btn:hover::before {
    left: 100%;
}

.current-model-name {
    flex: 1;
