/* –£–º–µ–Ω—å—à–∞–µ–º line-height –¥–ª—è –∫–æ–º–ø–∞–∫—Ç–Ω–æ—Å—Ç–∏ */
.sidebar * {
    line-height: 1.2 \!important;
}

/* –†–∞–∑–Ω—ã–µ –≤—ã—Å–æ—Ç—ã –¥–ª—è —Ä–∞–∑–Ω—ã—Ö –±–ª–æ–∫–æ–≤ */
.settings-block:nth-of-type(1) {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.05) 0%, rgba(139, 92, 246, 0.05) 100%);
    border-radius: 8px;
    padding: 8px;
    margin-bottom: 10px;
}

.mode-buttons {
    background: linear-gradient(135deg, rgba(255, 107, 107, 0.05) 0%, rgba(254, 202, 87, 0.05) 100%);
    border-radius: 8px;
    padding: 8px;
    display: flex;
    gap: 4px;
}

/* GENERATION –±–ª–æ–∫ –≤—ã—à–µ */
.sidebar > h3:nth-of-type(1) + .settings-block {
    min-height: 120px;
}

/* MODE –±–ª–æ–∫ –∫–æ–º–ø–∞–∫—Ç–Ω–µ–µ */
.sidebar > h3:nth-of-type(2) + .mode-buttons {
    min-height: 40px;
}

/* CONTEXT –±–ª–æ–∫ —Å—Ä–µ–¥–Ω–∏–π */
.sidebar > h3:nth-of-type(3) + textarea {
    min-height: 70px \!important;
    max-height: 100px \!important;
    background: linear-gradient(135deg, rgba(16, 185, 129, 0.05) 0%, rgba(59, 130, 246, 0.05) 100%);
}

/* MODEL –±–ª–æ–∫ –º–∞–ª–µ–Ω—å–∫–∏–π */
.sidebar > h3:nth-of-type(4) + select {
    height: 32px \!important;
    background: linear-gradient(135deg, rgba(251, 146, 60, 0.05) 0%, rgba(254, 202, 87, 0.05) 100%);
}

/* MEMORY FORGE –±–ª–æ–∫ –±–æ–ª—å—à–æ–π */
.memory-controls {
    min-height: 180px;
    background: linear-gradient(135deg, rgba(139, 92, 246, 0.08) 0%, rgba(255, 107, 107, 0.08) 100%);
    border: 1px solid rgba(255, 107, 107, 0.2);
    animation: forge-glow 3s ease-in-out infinite alternate;
}

@keyframes forge-glow {
    0% { box-shadow: 0 0 10px rgba(255, 107, 107, 0.2); }
    100% { box-shadow: 0 0 20px rgba(255, 107, 107, 0.4); }
}

/* –†–∞–∑–Ω—ã–µ –æ—Ç—Å—Ç—É–ø—ã –¥–ª—è –≤–∏–∑—É–∞–ª—å–Ω–æ–≥–æ —Ä–∏—Ç–º–∞ */
.sidebar > h3:nth-child(odd) {
    margin-top: 12px \!important;
}

.sidebar > h3:nth-child(even) {
    margin-top: 6px \!important;
}

