#!/usr/bin/env node

const express = require('express');
const { GoogleGenerativeAI } = require('@google/generative-ai');
const KeyRotator = require('./GalaxyDevelopersAI-key-rotator');
const { execSync, spawn } = require('child_process');
const fs = require('fs');
const path = require('path');
const axios = require('axios');

// Загружаем конфигурацию Gemini
const geminiConfig = JSON.parse(fs.readFileSync(path.join(__dirname, 'gemini-config.json'), 'utf8'));

// Автоматическая очистка macOS метаданных при запуске
try {
  execSync('find /Volumes/Z7S/development/GalaxyDevelopers/DevSystem -name "._*" -delete 2>/dev/null');
} catch (e) {
  // Игнорируем ошибки очистки
}

// Настоящие логи вместо заглушек
const log = console.log;
const debug = console.debug;
const info = console.info;
const error = console.error;

const app = express();
app.use(express.json({ limit: '50mb' }));

// Initialize key rotator
const keyRotator = new KeyRotator();

// Memory API configuration
const MEMORY_API_URL = 'http://127.0.0.1:37778';
let memoryAPIAvailable = false;

// Запускаем Memory API сервер
const startMemoryAPI = () => {
  const memoryProcess = spawn('python3', [
    path.join(__dirname, '..', 'memory', 'memory_api.py')
  ], {
    detached: false,
    stdio: 'inherit'
  });

  memoryProcess.on('error', (err) => {
    error('Failed to start Memory API:', err.message);
  });

  // Проверяем доступность Memory API через 2 секунды
  setTimeout(async () => {
    try {
      const response = await axios.get(`${MEMORY_API_URL}/health`);
      if (response.data.status === 'healthy') {
        memoryAPIAvailable = true;
        error('✅ Memory API is running on port 37778');
      }
    } catch (err) {
      error('⚠️ Memory API not available, running without memory');
    }
  }, 2000);
};

// Запускаем Memory API при старте
startMemoryAPI();

// Webhook state for iTerm2
let lastScreenshot = null;
let screenshotPending = false;

// CORS для локальной разработки
app.use((req, res, next) => {
  res.header('Access-Control-Allow-Origin', '*');
  res.header('Access-Control-Allow-Methods', 'POST, OPTIONS');
  res.header('Access-Control-Allow-Headers', 'Content-Type');
  if (req.method === 'OPTIONS') {
    return res.sendStatus(200);
  }
  next();
});

// Статические файлы
app.use(express.static(path.join(__dirname, '..')));

// Redirect root to web interface
app.get('/', (req, res) => {
  res.redirect('/interface/GalaxyDevelopersAI-web.html');
});

// Endpoint для получения списка моделей
app.get('/models', (req, res) => {
  const models = require('../resources/available-models.json');
  res.json(models);
});

// Endpoint для получения текущей конфигурации
app.get('/config', (req, res) => {
  res.json({
    gemini: geminiConfig,
    current_mode: geminiConfig.forge_mode.enabled ? 'forge' : 'default',
    temperature: geminiConfig.forge_mode.temperature,
    models: require('../resources/available-models.json')
  });
});

// Screenshot endpoint
app.post('/screenshot', (req, res) => {
  try {
    const timestamp = new Date().toISOString().replace(/:/g, '-').substring(0, 19);
    const filename = `screenshot-${timestamp}.png`;
    const screenshotPath = path.join(__dirname, '..', 'connectors', 'ScreenShots', filename);

    // Ensure directory exists
    if (!fs.existsSync(path.join(__dirname, '..', 'connectors', 'ScreenShots'))) {
      fs.mkdirSync(path.join(__dirname, '..', 'connectors', 'ScreenShots'), { recursive: true });
    }

    // Take screenshot using screencapture command
    execSync(`screencapture -x "${screenshotPath}"`);

    // Set webhook state for iTerm2
    lastScreenshot = filename;
    screenshotPending = true;

    error(`Screenshot saved: ${filename}`);
    res.json({ success: true, filename });
  } catch (err) {
    error('Screenshot error:', err.message);
    res.status(500).json({ error: err.message });
  }
});

// Webhook endpoints for iTerm2
app.get('/webhook/status', (req, res) => {
  res.json({
    new_screenshot: screenshotPending,
    filename: lastScreenshot
  });
});

app.post('/webhook/clear', (req, res) => {
  screenshotPending = false;
  res.json({ success: true });
});

// Auto-insert screenshot for Claude terminals
app.post('/webhook/notify-claude', (req, res) => {
  const { filename } = req.body;
  if (filename) {
    lastScreenshot = filename;
    screenshotPending = true;
    res.json({ success: true, message: 'Claude terminals will be notified' });
  } else {
    res.status(400).json({ error: 'No filename provided' });
  }
});

// Храним последние данные элемента для webhook
let lastElementData = null;

// Element selector endpoint для отправки в терминал
app.post('/element-selected', (req, res) => {
  const data = req.body;
  if (data) {
    // Сохраняем данные для webhook
    lastElementData = data;

    // Выводим полученные данные в консоль чтобы Claude их увидел
    error('=== ELEMENT DATA FOR CLAUDE ===');
    error(JSON.stringify(data, null, 2));
    error('=== END ELEMENT DATA ===');

    res.json({ success: true, message: 'Element sent to terminal' });
  } else {
    res.status(400).json({ error: 'No data provided' });
  }
});

// Webhook для iTerm2 интеграции - получение данных элемента
app.get('/webhook/element', (req, res) => {
  if (lastElementData) {
    res.json({ 
      hasData: true, 
      data: lastElementData 
    });
    // Очищаем после отправки
    lastElementData = null;
  } else {
    res.json({ hasData: false });
  }
});

// Memory API proxy endpoints
app.post('/memory/save', async (req, res) => {
  if (!memoryAPIAvailable) {
    return res.status(503).json({ error: 'Memory API not available' });
  }

  try {
    const response = await axios.post(`${MEMORY_API_URL}/save_conversation`, req.body);
    res.json(response.data);
  } catch (err) {
    error('Memory save error:', err.message);
    res.status(500).json({ error: err.message });
  }
});

app.get('/memory/context', async (req, res) => {
  if (!memoryAPIAvailable) {
    return res.json({ context: [] });
  }

  try {
    const response = await axios.get(`${MEMORY_API_URL}/get_context`, {
      params: req.query
    });
    res.json(response.data);
  } catch (err) {
    error('Memory context error:', err.message);
    res.json({ context: [] });
  }
});

app.post('/memory/knowledge', async (req, res) => {
  if (!memoryAPIAvailable) {
    return res.status(503).json({ error: 'Memory API not available' });
  }

  try {
    const response = await axios.post(`${MEMORY_API_URL}/save_knowledge`, req.body);
    res.json(response.data);
  } catch (err) {
    error('Knowledge save error:', err.message);
    res.status(500).json({ error: err.message });
  }
});

app.get('/memory/knowledge', async (req, res) => {
  if (!memoryAPIAvailable) {
    return res.json({ knowledge: [] });
  }

  try {
    const response = await axios.get(`${MEMORY_API_URL}/get_knowledge`, {
      params: req.query
    });
    res.json(response.data);
  } catch (err) {
    error('Knowledge get error:', err.message);
    res.json({ knowledge: [] });
  }
});

app.post('/memory/search', async (req, res) => {
  if (!memoryAPIAvailable) {
    return res.json({ conversations: [], knowledge: [] });
  }

  try {
    const response = await axios.post(`${MEMORY_API_URL}/search_memory`, req.body);
    res.json(response.data);
  } catch (err) {
    error('Memory search error:', err.message);
    res.json({ conversations: [], knowledge: [] });
  }
});

// MCP endpoint for AI agents to trigger screenshot
app.get('/mcp/screenshot', (req, res) => {
  try {
    const timestamp = new Date().toISOString().replace(/:/g, '-').substring(0, 19);
    const filename = `mcp-screenshot-${timestamp}.png`;
    const screenshotPath = path.join(__dirname, '..', 'connectors', 'ScreenShots', filename);

    // Ensure directory exists
    if (!fs.existsSync(path.join(__dirname, '..', 'connectors', 'ScreenShots'))) {
      fs.mkdirSync(path.join(__dirname, '..', 'connectors', 'ScreenShots'), { recursive: true });
    }

    // Take screenshot
    execSync(`screencapture -x "${screenshotPath}"`);

    error(`MCP Screenshot saved: ${filename}`);
    res.json({ 
      success: true, 
      filename,
      path: screenshotPath,
      message: `Screenshot saved to ScreenShots/${filename}`
    });
  } catch (err) {
    error('MCP Screenshot error:', err.message);
    res.status(500).json({ error: err.message });
  }
});

// Один endpoint - и всё
app.post('/chat', async (req, res) => {
  try {
    const { prompt, instruction, context, model, temperature, topK, topP, maxTokens, useMemory } = req.body;

    if (!prompt) {
      return res.status(400).json({ error: 'No prompt provided' });
    }

    // Получаем контекст из памяти если включено
    let memoryContext = '';
    if (useMemory && memoryAPIAvailable) {
      try {
        const contextResponse = await axios.get(`${MEMORY_API_URL}/get_context?limit=5`);
        if (contextResponse.data.context && contextResponse.data.context.length > 0) {
          memoryContext = '\n\nПРЕДЫДУЩИЙ КОНТЕКСТ:\n';
          contextResponse.data.context.forEach(c => {
            memoryContext += `[${c.timestamp}]\nUser: ${c.user}\nAssistant: ${c.assistant}\n\n`;
          });
        }
      } catch (err) {
        error('Failed to get memory context:', err.message);
      }
    }

    // Каждый раз новый клиент и новый ключ
    let apiKey;
    try {
      apiKey = keyRotator.getNextValidKey();
      log(`Using key: ${apiKey.substring(0, 10)}...${apiKey.substring(apiKey.length - 4)}`);
    } catch (e) {
      // Используем ключ из окружения если ротатор не работает
      apiKey = process.env.GEMINI_API_KEY || process.env.GOOGLE_API_KEY;
      if (!apiKey) {
        error('ERROR: No API keys available! Set GEMINI_API_KEY environment variable');
        return res.status(500).json({ error: 'No API keys available. Please set GEMINI_API_KEY' });
      }
      log(`Using environment key: ${apiKey.substring(0, 10)}...`);
    }

    const genAI = new GoogleGenerativeAI(apiKey);

    // Собираем полный промпт с памятью
    const fullPrompt = [
      instruction ? `INSTRUCTION:\n${instruction}\n` : '',
      memoryContext,  // Добавляем контекст из памяти
      context ? `CONTEXT:\n${context}\n` : '',
      `REQUEST:\n${prompt}`
    ].filter(Boolean).join('\n');

    // Используем выбранную модель или дефолтную
    const selectedModel = model || 'gemini-2.5-flash';
    log(`Using model: ${selectedModel}`);

    // Для streaming ответов - ВРЕМЕННО ОТКЛЮЧЕНО из-за ошибки API
    // TODO: Исправить когда Google починит generateContentStream
    /*
    if (req.body.stream) {
      // Streaming пока не работает с текущей версией API
    }
    */

    // Используем forge_mode конфигурацию если включен
    const config = geminiConfig.forge_mode.enabled ? geminiConfig.forge_mode : geminiConfig.default_generation_config;

    // Обычный режим - отправляем и забываем
    const genModel = genAI.getGenerativeModel({ 
      model: selectedModel,
      generationConfig: {
        temperature: temperature !== undefined ? temperature : config.temperature,
        topK: topK !== undefined ? topK : config.topK,
        topP: topP !== undefined ? topP : config.topP,
        maxOutputTokens: maxTokens || config.maxOutputTokens,
        stopSequences: config.stopSequences || [],
      }
    });

    log(`Generation config: temp=${temperature ?? config.temperature}, topK=${topK ?? config.topK}, topP=${topP ?? config.topP}`);
    const result = await genModel.generateContent(fullPrompt);

    // Проверяем что есть ответ
    const response = result.response.text();

    // Отдаем ответ и разрываем соединение
    res.json({ response });

    // Убиваем все ссылки
    delete genAI;
    delete model;
    delete result;

  } catch (err) {
    error('API Error:', err.message);
    error('Full error:', err);

    // If quota exceeded, mark key and retry with next one
    if (err.message && (err.message.includes('quota') || err.message.includes('limit'))) {
      keyRotator.markKeyAsExhausted(apiKey);
      error('Key exhausted, will use next one on next request');
    }

    res.status(500).json({ error: err.message });
  }
});

const PORT = process.env.PORT || 37777;
app.listen(PORT, '127.0.0.1', () => {
  error(`Backend running on http://127.0.0.1:${PORT}`);
  error('POST /chat - {prompt, instruction, context}');
});

// Graceful shutdown
process.on('SIGTERM', () => process.exit(0));
process.on('SIGINT', () => process.exit(0));

