    transition: all 0.2s ease;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

#screenshot-btn:hover {
    background: rgba(99, 102, 241, 0.1);
    border-color: var(--accent-primary);
    transform: translateY(-50%) scale(1.05);
}

/* Tools panel */
.tools-panel {
    position: absolute;
    right: 30px;
    top: 50%;
    transform: translateY(-50%);
    display: flex;
    gap: 10px;
    align-items: center;
}

.tool-btn {
    width: 28px;
    height: 28px;
    border-radius: 50%;
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    color: var(--text-primary);
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 14px;
    transition: all 0.2s ease;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.tool-btn:hover {
    background: rgba(99, 102, 241, 0.1);
    border-color: var(--accent-primary);
    transform: scale(1.1);
}

.tool-btn.active {
    background: var(--accent-gradient);
    border-color: transparent;
}

.sub-tools {
    display: none;
    gap: 8px;
}

.sub-tools.show {
    display: flex;
}

/* Element selector mode */
body.selector-mode * {
    cursor: crosshair !important;
}

body.selector-mode *:hover {
    outline: 2px solid var(--accent-primary) !important;
    outline-offset: 2px;
}

/* Streaming text */
@keyframes thinking {
    0%, 100% { opacity: 0.3; }
    50% { opacity: 1; }
}

.thinking-indicator {
    color: var(--accent-primary);
    font-style: italic;
    animation: thinking 1.5s ease-in-out infinite;
}

/* Попап для комментария к элементу */
.comment-popup {
    position: fixed;
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 12px;
    padding: 16px;
    z-index: 10000;
    min-width: 300px;
    box-shadow: 0 10px 40px rgba(0,0,0,0.5);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
}

.comment-popup textarea {
    width: 100%;
    min-height: 80px;
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    color: var(--text-primary);
    border-radius: 8px;
    padding: 8px;
    margin: 8px 0;
    resize: none;
    font-family: inherit;
}

.comment-popup-buttons {
    display: flex;
    gap: 8px;
    justify-content: flex-end;
}

.comment-popup button {
    padding: 6px 12px;
    border-radius: 6px;
    border: 1px solid var(--border-color);
    background: var(--bg-card);
    color: var(--text-primary);
    cursor: pointer;
    transition: all 0.2s;
}

.comment-popup button:hover {
    background: var(--accent-primary);
    border-color: var(--accent-primary);
}

.comment-popup-title {
    font-size: 12px;
    color: var(--text-secondary);
    margin-bottom: 8px;
}

/* ====== FORGE MEMORY STYLES ====== */
.memory-title {
    color: #ff6b6b;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-top: 20px;
    margin-bottom: 15px;
    text-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
    animation: forge-pulse 2s ease-in-out infinite;
}

@keyframes forge-pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.8; }
}

.memory-controls {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.05) 0%, rgba(139, 92, 246, 0.05) 100%);
    border-radius: 12px;
    padding: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
}

.memory-toggle {
    margin-bottom: 12px;
}

/* FORGE Style Toggle Switch */
.forge-switch {
    position: relative;
    display: block;
    width: 100%;
    height: 45px;
    cursor: pointer;
}

.forge-switch input {
    opacity: 0;
    width: 0;
    height: 0;
}

.forge-slider {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(30, 30, 35, 0.9) 0%, rgba(40, 40, 45, 0.9) 100%);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 8px;
    transition: all 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
    overflow: hidden;
    display: flex;
    align-items: center;
    padding: 0 15px;
}

.forge-slider::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #ff6b6b 0%, #ff8787 50%, #feca57 100%);
    transition: left 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
    opacity: 0.3;
}

.forge-switch input:checked + .forge-slider {
    background: linear-gradient(135deg, rgba(255, 107, 107, 0.1) 0%, rgba(255, 135, 135, 0.1) 100%);
    border-color: rgba(255, 107, 107, 0.5);
    box-shadow: 0 0 20px rgba(255, 107, 107, 0.3);
}

.forge-switch input:checked + .forge-slider::before {
    left: 0;
}

.forge-label {
    position: relative;
    z-index: 2;
    font-size: 13px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
    transition: all 0.3s ease;
}

.forge-switch input:checked + .forge-slider .forge-label {
    color: #fff;
    text-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
}

/* FORGE Action Button */
.forge-action-btn {
    width: 100%;
    margin-top: 15px;
    padding: 12px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border: none;
    border-radius: 8px;
    color: white;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    transition: all 0.3s ease;
}

.forge-action-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transition: left 0.6s ease;
}

.forge-action-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(102, 126, 234, 0.4);
}

.forge-action-btn:hover::before {
    left: 100%;
}

.forge-btn-text {
    position: relative;
    z-index: 2;
}

/* Анимация при активации */
@keyframes forge-activate {
    0% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
    }
}

.forge-switch input:checked + .forge-slider {
    animation: forge-activate 0.3s ease;
}


/* ====== FORGE MODEL SELECTOR POPUP ====== */
.send-controls {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.model-selector-btn {
    padding: 10px 16px;
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(139, 92, 246, 0.1) 100%);
    border: 1px solid rgba(99, 102, 241, 0.3);
    border-radius: 10px;
    color: var(--text-primary);
    cursor: pointer;
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-size: 12px;
    font-weight: 600;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.model-selector-btn::before {
    content: "";
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(99, 102, 241, 0.2), transparent);
    transition: left 0.6s ease;
}

.model-selector-btn:hover {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.2) 0%, rgba(139, 92, 246, 0.2) 100%);
    transform: translateY(-2px);
    box-shadow: 0 5px 20px rgba(99, 102, 241, 0.3);
}

.model-selector-btn:hover::before {
    left: 100%;
}

.current-model-name {
    flex: 1;
    text-align: left;
}

.model-arrow {
    margin-left: 10px;
    transition: transform 0.3s ease;
}

.model-selector-btn.active .model-arrow {
    transform: rotate(180deg);
}

/* Model Popup */
.model-popup {
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%) translateY(-20px);
    width: 600px;
    max-height: 500px;
    background: linear-gradient(135deg, rgba(20, 20, 25, 0.98) 0%, rgba(30, 30, 35, 0.98) 100%);
    border: 1px solid rgba(99, 102, 241, 0.3);
    border-radius: 16px;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.8), 0 0 40px rgba(99, 102, 241, 0.2);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    z-index: 1000;
    display: none;
    animation: popupSlideIn 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

.model-popup.show {
    display: block;
}

@keyframes popupSlideIn {
    from {
        opacity: 0;
        transform: translateX(-50%) translateY(10px) scale(0.95);
    }
    to {
        opacity: 1;
        transform: translateX(-50%) translateY(-20px) scale(1);
    }
}

.model-popup-header {
    padding: 16px 20px;
    border-bottom: 1px solid rgba(99, 102, 241, 0.2);
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(139, 92, 246, 0.1) 100%);
    border-radius: 16px 16px 0 0;
}

.model-popup-title {
    font-size: 14px;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 2px;
    background: linear-gradient(135deg, #ff6b6b 0%, #feca57 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    text-shadow: 0 0 20px rgba(255, 107, 107, 0.5);
}

.model-popup-close {
    width: 28px;
    height: 28px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
    color: var(--text-secondary);
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all 0.3s ease;
    font-size: 16px;
}

.model-popup-close:hover {
    background: rgba(239, 68, 68, 0.2);
    border-color: rgba(239, 68, 68, 0.5);
    color: #ef4444;
    transform: rotate(90deg);
}

.model-popup-content {
    padding: 20px;
    max-height: 400px;
    overflow-y: auto;
}

.model-popup-content::-webkit-scrollbar {
    width: 6px;
}

.model-popup-content::-webkit-scrollbar-track {
    background: transparent;
}

.model-popup-content::-webkit-scrollbar-thumb {
    background: rgba(99, 102, 241, 0.3);
    border-radius: 3px;
}

.model-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 12px;
}

.model-card {
    padding: 16px;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.02) 0%, rgba(255, 255, 255, 0.05) 100%);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 12px;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.model-card::before {
    content: "";
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(99, 102, 241, 0.1), transparent);
    transition: left 0.6s ease;
}

.model-card:hover {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(139, 92, 246, 0.1) 100%);
    border-color: rgba(99, 102, 241, 0.5);
    transform: translateY(-4px) scale(1.02);
    box-shadow: 0 10px 30px rgba(99, 102, 241, 0.3);
}

.model-card:hover::before {
    left: 100%;
}

.model-card.selected {
    background: linear-gradient(135deg, rgba(255, 107, 107, 0.15) 0%, rgba(254, 202, 87, 0.15) 100%);
    border-color: rgba(255, 107, 107, 0.5);
    box-shadow: 0 0 20px rgba(255, 107, 107, 0.3);
}

.model-card-name {
    font-size: 13px;
    font-weight: 600;
    color: var(--text-primary);
    margin-bottom: 4px;
}

.model-card-desc {
    font-size: 11px;
    color: var(--text-secondary);
    margin-bottom: 8px;
}

.model-card-tokens {
    font-size: 10px;
    color: rgba(99, 102, 241, 0.8);
    font-weight: 500;
    display: flex;
    align-items: center;
    gap: 4px;
}

.model-card-tokens::before {
    content: "⚡";
    font-size: 12px;
}

.model-card.recommended {
    border-color: rgba(255, 107, 107, 0.3);
}

.model-card.recommended::after {
    content: "🔥 FORGE";
    position: absolute;
    top: 8px;
