/* Pipeline Status Panel */
.pipeline-status {
    background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
    border-radius: 12px;
    padding: 16px;
    margin-bottom: 24px;
    border: 1px solid rgba(102, 126, 234, 0.2);
}

.pipeline-status h3 {
    color: var(--text-primary);
    font-size: 12px;
    font-weight: 600;
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.pipeline-stages {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.stage {
    display: flex;
    align-items: center;
    padding: 10px 12px;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 8px;
    transition: all 0.3s ease;
    border: 1px solid transparent;
    cursor: pointer;
}

.stage:hover {
    background: rgba(255, 255, 255, 0.05);
    border-color: rgba(102, 126, 234, 0.3);
    transform: translateX(4px);
}

.stage.active {
    background: linear-gradient(135deg, rgba(102, 126, 234, 0.2) 0%, rgba(118, 75, 162, 0.2) 100%);
    border-color: rgba(102, 126, 234, 0.4);
}

.stage-icon {
    font-size: 16px;
    margin-right: 10px;
}

.stage-name {
    flex: 1;
    font-size: 11px;
    font-weight: 500;
    color: var(--text-secondary);
}

.stage.active .stage-name {
    color: var(--text-primary);
}

.stage-status {
    font-size: 14px;
}

/* Agent Status Panel */
.agent-status {
    background: linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(59, 130, 246, 0.1) 100%);
    border-radius: 12px;
    padding: 16px;
    margin-bottom: 24px;
    border: 1px solid rgba(16, 185, 129, 0.2);
}

.agent-status h3 {
    color: var(--text-primary);
    font-size: 12px;
    font-weight: 600;
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.agent-list {
    display: flex;
    flex-direction: column;
    gap: 6px;
}

.agent-item {
    display: flex;
    align-items: center;
    padding: 8px 10px;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 6px;
    font-size: 11px;
    transition: all 0.2s ease;
}

.agent-item:hover {
    background: rgba(255, 255, 255, 0.05);
}

.agent-indicator {
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: var(--text-muted);
    margin-right: 10px;
    position: relative;
}

.agent-item.active .agent-indicator {
    background: #10b981;
    animation: pulse 2s ease-in-out infinite;
}

.agent-item.active .agent-indicator::after {
    content: '';
    position: absolute;
    top: -4px;
    left: -4px;
    right: -4px;
    bottom: -4px;
    border-radius: 50%;
    background: rgba(16, 185, 129, 0.3);
    animation: ripple 2s ease-in-out infinite;
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.6; }
}

@keyframes ripple {
    0% { transform: scale(1); opacity: 1; }
    100% { transform: scale(1.5); opacity: 0; }
}

.agent-item > span:nth-child(2) {
    flex: 1;
    color: var(--text-secondary);
    font-weight: 500;
}

.agent-item.active > span:nth-child(2) {
    color: var(--text-primary);
}

.agent-task {
    font-size: 10px;
    color: var(--text-muted);
    font-style: italic;
}

/* Generation Settings */
.generation-settings {
    background: rgba(40, 44, 52, 0.6);
    border-radius: 8px;
    padding: 15px;
    margin-bottom: 20px;
}

.generation-settings h3 {
    color: #00ff88;
    margin-bottom: 15px;
    font-size: 14px;
}

.setting-item {
    display: flex;
    align-items: center;
    margin-bottom: 10px;
    gap: 10px;
}

.setting-item label {
    color: #8892b0;
    width: 100px;
    font-size: 12px;
}

.setting-item input[type="range"] {
    flex: 1;
    height: 4px;
    background: #1e2127;
    border-radius: 2px;
    outline: none;
}

.setting-item input[type="range"]::-webkit-slider-thumb {
    width: 16px;
    height: 16px;
    background: #00ff88;
    border-radius: 50%;
    cursor: pointer;
    -webkit-appearance: none;
}

