   120‚Üí    top: 0;
   121‚Üí    left: 0;
   122‚Üí    width: 100%;
   123‚Üí    height: 100%;
   124‚Üí    background: linear-gradient(90deg, 
   125‚Üí        transparent 0%,
   126‚Üí        transparent 10%,
   127‚Üí        var(--accent-primary) 50%,
   128‚Üí        transparent 90%,
   129‚Üí        transparent 100%);
