}

.mode-btn.active {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border-color: #667eea;
}

.mode-btn:hover:not(.active) {
    border-color: #00ff88;
    color: #00ff88;
}

