/* Generation Settings */
.settings-block {
    margin-bottom: 20px;
}

.setting-row {
    display: flex;
    align-items: center;
    margin-bottom: 10px;
    gap: 10px;
}

.setting-label {
    color: #8892b0;
    width: 80px;
    font-size: 10px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.setting-row input[type="range"] {
    flex: 1;
}

.setting-row input[type="number"] {
    width: 80px;
}

.value {
    color: #00ff88;
    font-size: 11px;
    min-width: 30px;
    text-align: right;
}

.mode-buttons {
    display: flex;
    gap: 5px;
    margin-bottom: 20px;
}

.mode-btn {
    flex: 1;
    padding: 8px;
    background: rgba(40, 44, 52, 0.6);
    border: 1px solid rgba(0, 255, 136, 0.2);
    border-radius: 4px;
    color: #8892b0;
    font-size: 11px;
    cursor: pointer;
    transition: all 0.3s ease;
}

.mode-btn:hover {
    background: rgba(0, 255, 136, 0.1);
    border-color: #00ff88;
}

.mode-btn.active {
    background: linear-gradient(135deg, rgba(0, 255, 136, 0.2) 0%, rgba(0, 178, 255, 0.2) 100%);
    border-color: #00ff88;
    color: #00ff88;
}

