file.content:
:root {
    --bg-primary: #0a0a0a;
    --bg-secondary: #141414;
    --bg-tertiary: #1a1a1a;
    --bg-card: rgba(255, 255, 255, 0.03);
    --border-color: rgba(255, 255, 255, 0.12);
    --text-primary: #ffffff;
    --text-secondary: #a0a0a0;
    --text-muted: #666666;
    --accent-primary: #6366f1;
    --accent-secondary: #8b5cf6;
    --accent-gradient: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    --success: #10b981;
    --error: #ef4444;
    --warning: #f59e0b;
    --tab-height: 35px;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    background: var(--bg-primary);
    color: var(--text-primary);
    height: 100vh;
    display: flex;
    flex-direction: column;
    position: relative;
}

body::before {
    content: '';
    position: fixed;
    top: -50%;
    right: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(99, 102, 241, 0.1) 0%, transparent 70%);
    pointer-events: none;
    animation: float 20s ease-in-out infinite;
}

@keyframes float {
    0%, 100% { transform: translate(0, 0) rotate(0deg); }
    33% { transform: translate(-30px, -30px) rotate(120deg); }
    66% { transform: translate(30px, -30px) rotate(240deg); }
}

.header {
    padding: 10px 30px;
    border-bottom: 1px solid var(--border-color);
    background: rgba(20, 20, 20, 0.8);
    backdrop-filter: blur(20px);
    -webkit-backdrop-filter: blur(20px);
    position: relative;
    z-index: 100;
}

.header h1 {
    font-size: 20px;
    font-weight: 600;
    background: var(--accent-gradient);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    letter-spacing: -0.5px;
}

.wrapper {
    flex: 1;
    display: flex;
    overflow: hidden;
    position: relative;
}

.sidebar {
    width: 320px;
    background: rgba(20, 20, 20, 0.5);
    border-right: 1px solid var(--border-color);
    padding: 24px;
    overflow-y: auto;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.sidebar::-webkit-scrollbar {
    width: 6px;
}

.sidebar::-webkit-scrollbar-track {
    background: transparent;
}

.sidebar::-webkit-scrollbar-thumb {
    background: var(--border-color);
    border-radius: 3px;
}

.sidebar::-webkit-scrollbar-thumb:hover {
    background: var(--text-muted);
}

/* Неоновая полоса статуса */
.status-strip {
    height: 3px;
    width: 100%;
    position: relative;
    overflow: hidden;
    background: transparent;
    border-bottom: 1px solid var(--border-color);
}

.status-strip::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--accent-primary) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(99, 102, 241, 0.5), 
               0 0 16px rgba(99, 102, 241, 0.3);
    transition: all 0.3s ease;
}

.status-strip.error::before {
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--error) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(239, 68, 68, 0.5), 
               0 0 16px rgba(239, 68, 68, 0.3);
}

.status-strip.warning::before {
    background: linear-gradient(90deg, 
        transparent 0%,
        transparent 10%,
        var(--warning) 50%,
        transparent 90%,
        transparent 100%);
    box-shadow: 0 0 8px rgba(245, 158, 11, 0.5), 
               0 0 16px rgba(245, 158, 11, 0.3);
}

.status-strip.loading::before {
    background: linear-gradient(90deg, 
        transparent 0%, 
        var(--accent-primary) 50%, 
        transparent 100%);
    animation: statusFlow 2s ease-in-out infinite;
}

@keyframes statusFlow {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
}

/* Proximity язычок - ПОЛНОСТЬЮ СКРЫТ изначально */
.proximity-instruction {
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%) translateY(-100%);
    z-index: 60;
    overflow: visible;
    width: fit-content;
    min-width: 200px;
}

/* Язычок */
.instruction-tab {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border: none;
    border-radius: 0 0 12px 12px;
    padding: 8px 24px;
    cursor: pointer;
    font-size: 12px;
    color: white;
    transition: all 0.3s ease;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    text-align: center;
    height: var(--tab-height);
    display: flex;
    align-items: center;
    justify-content: center;
    user-select: none;
    white-space: nowrap;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    box-shadow: 0 4px 15px rgba(102, 126, 234, 0.4);
    position: relative;
    overflow: hidden;
}

.instruction-tab::before {
    content: '📝';
    margin-right: 6px;
    font-size: 14px;
}

.instruction-tab::after {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s ease;
}

.instruction-tab:hover {
    background: linear-gradient(135deg, #764ba2 0%, #667eea 100%);
    transform: scale(1.05);
    box-shadow: 0 6px 20px rgba(102, 126, 234, 0.5);
}

.instruction-tab:hover::after {
    left: 100%;
}

/* Состояние когда панель раскрыта по клику */
.proximity-instruction.expanded {
    width: 70%;
}

.proximity-instruction.expanded .instruction-tab {
    background: var(--bg-secondary);
    border-bottom: 1px solid var(--border-color);
    cursor: pointer;
}

.proximity-instruction.expanded .instruction-panel {
    display: block;
}


/* Панель с textarea - скрыта по умолчанию */
.instruction-panel {
    padding: 10px;
    display: none;
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 0 0 8px 8px;
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
}

.instruction-panel textarea {
    width: calc(100% - 20px);
    height: 100px;
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    padding: 10px;
    color: var(--text-primary);
    resize: vertical;
    font-family: inherit;
    font-size: 13px;
    outline: none;
}

.instruction-panel textarea:focus {
    outline: none;
    border-color: var(--accent-primary);
}

.instruction-panel textarea::placeholder {
    color: var(--text-secondary);
}

.content {
    flex: 1;
    display: flex;
    flex-direction: column;
    position: relative;
}

/* Pipeline Status Panel */
.pipeline-status {
    background: linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%);
    border-radius: 12px;
    padding: 16px;
    margin-bottom: 24px;
    border: 1px solid rgba(102, 126, 234, 0.2);
}

.pipeline-status h3 {
    color: var(--text-primary);
    font-size: 12px;
    font-weight: 600;
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.pipeline-stages {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.stage {
    display: flex;
    align-items: center;
    padding: 10px 12px;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 8px;
    transition: all 0.3s ease;
    border: 1px solid transparent;
    cursor: pointer;
}

.stage:hover {
    background: rgba(255, 255, 255, 0.05);
    border-color: rgba(102, 126, 234, 0.3);
    transform: translateX(4px);
}

.stage.active {
    background: linear-gradient(135deg, rgba(102, 126, 234, 0.2) 0%, rgba(118, 75, 162, 0.2) 100%);
    border-color: rgba(102, 126, 234, 0.4);
}

.stage-icon {
    font-size: 16px;
    margin-right: 10px;
}

.stage-name {
    flex: 1;
    font-size: 11px;
    font-weight: 500;
    color: var(--text-secondary);
}

.stage.active .stage-name {
    color: var(--text-primary);
}

.stage-status {
    font-size: 14px;
}

/* Agent Status Panel */
.agent-status {
    background: linear-gradient(135deg, rgba(16, 185, 129, 0.1) 0%, rgba(59, 130, 246, 0.1) 100%);
    border-radius: 12px;
    padding: 16px;
    margin-bottom: 24px;
    border: 1px solid rgba(16, 185, 129, 0.2);
}

.agent-status h3 {
    color: var(--text-primary);
    font-size: 12px;
    font-weight: 600;
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.agent-list {
    display: flex;
    flex-direction: column;
    gap: 6px;
}

.agent-item {
    display: flex;
    align-items: center;
    padding: 8px 10px;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 6px;
    font-size: 11px;
    transition: all 0.2s ease;
}

.agent-item:hover {
    background: rgba(255, 255, 255, 0.05);
}

.agent-indicator {
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: var(--text-muted);
    margin-right: 10px;
    position: relative;
}

.agent-item.active .agent-indicator {
    background: #10b981;
    animation: pulse 2s ease-in-out infinite;
}

.agent-item.active .agent-indicator::after {
    content: '';
    position: absolute;
    top: -4px;
    left: -4px;
    right: -4px;
    bottom: -4px;
    border-radius: 50%;
    background: rgba(16, 185, 129, 0.3);
    animation: ripple 2s ease-in-out infinite;
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.6; }
}

@keyframes ripple {
    0% { transform: scale(1); opacity: 1; }
    100% { transform: scale(1.5); opacity: 0; }
}

.agent-item > span:nth-child(2) {
    flex: 1;
    color: var(--text-secondary);
    font-weight: 500;
}

.agent-item.active > span:nth-child(2) {
    color: var(--text-primary);
}

.agent-task {
    font-size: 10px;
    color: var(--text-muted);
    font-style: italic;
}

/* Generation Settings */
.generation-settings {
    background: rgba(40, 44, 52, 0.6);
    border-radius: 8px;
    padding: 15px;
    margin-bottom: 20px;
}

.generation-settings h3 {
    color: #00ff88;
    margin-bottom: 15px;
    font-size: 14px;
}

.setting-item {
    display: flex;
    align-items: center;
    margin-bottom: 10px;
    gap: 10px;
}

.setting-item label {
    color: #8892b0;
    width: 100px;
