/* Generation Settings Panel */
.settings-block {
    background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(139, 92, 246, 0.1) 100%);
    border-radius: 12px;
    padding: 16px;
    margin-bottom: 24px;
    border: 1px solid rgba(99, 102, 241, 0.2);
}

.setting-row {
    display: flex;
    align-items: center;
    padding: 10px 12px;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 8px;
    transition: all 0.3s ease;
    border: 1px solid transparent;
    margin-bottom: 8px;
}

.setting-row:hover {
    background: rgba(255, 255, 255, 0.05);
    border-color: rgba(99, 102, 241, 0.3);
    transform: translateX(4px);
}

.setting-label {
    width: 100px;
    font-size: 11px;
    font-weight: 500;
    color: var(--text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.setting-row input[type="range"] {
    flex: 1;
    height: 4px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
    outline: none;
    margin: 0 12px;
}

.setting-row input[type="range"]::-webkit-slider-thumb {
    width: 16px;
    height: 16px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border-radius: 50%;
    cursor: pointer;
    -webkit-appearance: none;
    box-shadow: 0 2px 8px rgba(99, 102, 241, 0.4);
    transition: all 0.2s ease;
}

.setting-row input[type="range"]::-webkit-slider-thumb:hover {
    transform: scale(1.2);
    box-shadow: 0 4px 12px rgba(99, 102, 241, 0.6);
}

.setting-row input[type="number"] {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(99, 102, 241, 0.3);
    color: var(--text-primary);
    padding: 6px 10px;
    border-radius: 6px;
    width: 80px;
    font-size: 11px;
    transition: all 0.2s ease;
}

.setting-row input[type="number"]:focus {
    border-color: rgba(99, 102, 241, 0.6);
    background: rgba(255, 255, 255, 0.08);
    outline: none;
}

.value {
    color: var(--accent-primary);
    font-size: 11px;
    min-width: 35px;
    text-align: right;
    font-weight: 600;
}

/* Mode Buttons */
.mode-buttons {
    display: flex;
    gap: 8px;
    margin-bottom: 24px;
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.1) 0%, rgba(245, 158, 11, 0.1) 100%);
    border-radius: 12px;
    padding: 16px;
    border: 1px solid rgba(239, 68, 68, 0.2);
}

.mode-btn {
    flex: 1;
    padding: 10px 8px;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid transparent;
    border-radius: 8px;
    color: var(--text-secondary);
    font-size: 11px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.mode-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
    transition: left 0.5s ease;
}

.mode-btn:hover {
    background: rgba(255, 255, 255, 0.05);
    border-color: rgba(239, 68, 68, 0.3);
    transform: translateY(-2px);
}

.mode-btn:hover::before {
    left: 100%;
}

.mode-btn.active {
    background: linear-gradient(135deg, rgba(239, 68, 68, 0.2) 0%, rgba(245, 158, 11, 0.2) 100%);
    border-color: rgba(239, 68, 68, 0.4);
    color: var(--text-primary);
    box-shadow: 0 4px 12px rgba(239, 68, 68, 0.3);
    animation: glow 2s ease-in-out infinite alternate;
}

@keyframes glow {
    from { box-shadow: 0 4px 12px rgba(239, 68, 68, 0.3); }
    to { box-shadow: 0 4px 20px rgba(239, 68, 68, 0.5); }
}

