/* Generation Settings */
.settings-block {
    margin-bottom: 20px;
}

.setting-row {
    display: flex;
    align-items: center;
    margin-bottom: 10px;
    gap: 10px;
}

.setting-label {
    color: #8892b0;
    width: 80px;
    font-size: 10px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

