   137‚Üí        transparent 0%,
   138‚Üí        transparent 10%,
   139‚Üí        var(--error) 50%,
   140‚Üí        transparent 90%,
   141‚Üí        transparent 100%);
   142‚Üí    box-shadow: 0 0 8px rgba(239, 68, 68, 0.5), 
   143‚Üí               0 0 16px rgba(239, 68, 68, 0.3);
   144‚Üí}
   145‚Üí
   146‚Üí.status-strip.warning::before {
   147‚Üí    background: linear-gradient(90deg, 
   148‚Üí        transparent 0%,
   149‚Üí        transparent 10%,
   150‚Üí        var(--warning) 50%,
   151‚Üí        transparent 90%,
   152‚Üí        transparent 100%);
   153‚Üí    box-shadow: 0 0 8px rgba(245, 158, 11, 0.5), 
   154‚Üí               0 0 16px rgba(245, 158, 11, 0.3);
   155‚Üí}
   156‚Üí
   157‚Üí.status-strip.loading::before {
   158‚Üí    background: linear-gradient(90deg, 
   159‚Üí        transparent 0%, 
   160‚Üí        var(--accent-primary) 50%, 
   161‚Üí        transparent 100%);
